import "./LoguinHome.css";
import {useState} from "react"

export const LoguinHome = (props) => {

    const [numero, setNumero] = useState(0);
    const [usuario, setUsuario] = useState("");
    // let numero = 0;

     const sumar = ()=>{
         setNumero(numero + 1);
    }

    const restar = ()=>{
        setNumero(numero - 1);
   }

   const [mostar, setMostrar] = useState(true);

   function mostrarOcultar(){
        setMostrar(!mostar);
   }


    return (
        <div className = "LoguinHome">

            Loguin
            <div></div>
                {props.nombre}
            <div></div>
                {props.mision}
            <hr></hr>
            <button onClick={restar}>resta 1</button>
            {numero}
            <button onClick={sumar}>Suma 1</button>
            <hr></hr>
            <button onClick={mostrarOcultar}>{mostar ? "ocultar" : "mostar"} </button>
            {mostar === true && <h2>"Hola mundo"</h2> }

            <input type="text" name = "Usuario"></input>
            <div></div>
            <input type="password" name = "Usuario"></input>
        </div>
        )
}
