      (resolucion) => {

      setProductos(resolucion);
        
       }
      )
       
  },[productos, setProductos])
  

    


  return (
    <div className="Homeppal">

      { 
      productos.length > 0 && 
      
        productos.map(
          (producto)=>{
            return(
              <div className="productos">
                 
              </div>
            )
          }       
        )
        

      }

    </div>
  );
}

export default Homeppal;
